import React, { useState } from 'react'
import { TextField, Button, Typography, Paper } from '@material-ui/core'
import useStyles from './styles'
import FileBase from 'react-file-base64'

const Form = () => {
    const [postData, setPostData] = useState({
        creator: '', title: '', message: '', tags: '', selectedFile: ''
    })
    const classes = useStyles();
    return (
        <Paper className={classes.paper}>
            <form onSubmit={handelSubmit} className={classes.form} noValidate autoComplete='off'></form>
            <Typography variant='h6'>Creating A Memory</Typography>
            <TextField name='creator' variant='outlined' label='Creator' value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value })} fullWidth></TextField>
            <TextField name='title' variant='outlined' label='Title' value={postData.creator} onChange={(e) => setPostData({ ...postData, title: e.target.value })} fullWidth></TextField>
            <TextField name='message' variant='outlined' label='Message' value={postData.creator} onChange={(e) => setPostData({ ...postData, message: e.target.value })} fullWidth></TextField>
            <TextField name='tags' variant='outlined' label='Tags' value={postData.creator} onChange={(e) => setPostData({ ...postData, tags: e.target.value })} fullWidth></TextField>

            <div className={classes.fileInput}>
                <FileBase
                    type='file'
                    multiple={false}
                    onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })}
                >
                </FileBase>
            </div>

        </Paper>
    )
}

export default Form
